    script-src 'self' 'nonce-$__JSX_EXPR_1__' 'strict-dynamic';
    style-src 'self' 'nonce-$__JSX_EXPR_2__';
    img-src 'self' blob: data:;
    font-src 'self';
    object-src 'none';
    base-uri 'self';
    form-action 'self';
    frame-ancestors 'none';
    upgrade-insecure-requests;
